import React, { useEffect, useState } from "react";
import axios from "axios";

function FileList() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    // Verifica se o token está no Local Storage
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Você precisa estar logado para acessar esta página.");
      window.location.href = "/";
      return;
    }
    const handleLogout = () => {
        localStorage.removeItem("token"); // Remove o token
        alert("Você foi deslogado.");
        window.location.href = "/"; // Redireciona para a tela de login
      };
    // Faz a requisição para buscar os arquivos
    const fetchFiles = async () => {
      try {
        const response = await axios.get("http://localhost:8000/files.php", {
          headers: {
            Authorization: `Bearer ${token}`, // Envia o token no cabeçalho
          },
        });
        setFiles(response.data);
      } catch (error) {
        console.error("Erro ao buscar arquivos:", error);
        alert("Erro ao carregar arquivos. Faça login novamente.");
        window.location.href = "/";
      }
    };

    fetchFiles();
  }, []);

  return (
    <div>
      <h1>Lista de Arquivos</h1>
      <ul>
        {files.map((file) => (
          <li key={file.id}>{file.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default FileList;
