import React, { useState } from "react";
import axios from "axios";

function CreateFile() {
  const [name, setName] = useState("");
  const [cnpj, setCnpj] = useState("");
  const [codigoConta, setCodigoConta] = useState("");
  const [saldoDia, setSaldoDia] = useState("");
  const [tipoRemessa, setTipoRemessa] = useState("I"); // Valor padrão como "I"

  const handleCreateFile = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const data = {
      name,
      cnpj,
      codigoConta,
      saldoDia,
      tipoRemessa,
    };

    try {
      await axios.post(
        "http://localhost:8000/files.php",
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      alert("Arquivo criado com sucesso!");
      window.location.href = "/files"; // Redireciona para a lista de arquivos
    } catch (error) {
      console.error("Erro ao criar arquivo:", error);
      alert("Erro ao criar arquivo. Tente novamente.");
    }
  };

  return (
    <div>
      <h1>Criar Novo Arquivo</h1>
      <form onSubmit={handleCreateFile}>
        <input
          type="text"
          placeholder="Nome do Arquivo"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="CNPJ"
          value={cnpj}
          onChange={(e) => setCnpj(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Código da Conta"
          value={codigoConta}
          onChange={(e) => setCodigoConta(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Saldo do Dia"
          value={saldoDia}
          onChange={(e) => setSaldoDia(e.target.value)}
          required
        />
        <label>
          Tipo de Remessa:
          <input
            type="checkbox"
            checked={tipoRemessa === "I"}
            onChange={() => setTipoRemessa(tipoRemessa === "I" ? "O" : "I")} // Alterna entre "I" e "O"
          />
          I
        </label>
        <button type="submit">Criar</button>
      </form>
    </div>
  );
}

export default CreateFile;
