import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./Header";
import { useParams } from "react-router-dom";

function EditFile() {
  const { id } = useParams(); // Obtém o ID da URL
  const [name, setName] = useState("");
  const [cnpj, setCnpj] = useState("");
  const [codigoConta1, setCodigoConta1] = useState("");
  const [saldoDia1, setSaldoDia1] = useState("");
  const [codigoConta2, setCodigoConta2] = useState("");
  const [saldoDia2, setSaldoDia2] = useState("");
  const [tipoRemessa, setTipoRemessa] = useState("I"); // Valor padrão como "I"
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFile = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get(`http://localhost:8000/files.php?id=${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const fileData = response.data.content; // Obtém o conteúdo do arquivo
        setName(response.data.name || ""); // Define o nome do arquivo
        setCnpj(fileData?.cnpj || ""); // Define o CNPJ
        setCodigoConta1(fileData?.contas?.[0]?.codigoConta || ""); // Conta 1 - Código
        setSaldoDia1(fileData?.contas?.[0]?.saldoDia || ""); // Conta 1 - Saldo
        setCodigoConta2(fileData?.contas?.[1]?.codigoConta || ""); // Conta 2 - Código
        setSaldoDia2(fileData?.contas?.[1]?.saldoDia || ""); // Conta 2 - Saldo
        setTipoRemessa(fileData?.tipoRemessa || "I"); // Tipo de Remessa
        setLoading(false); // Dados carregados
      } catch (error) {
        console.error("Erro ao buscar arquivo:", error);
        alert("Erro ao carregar arquivo.");
      }
    };

    fetchFile();
  }, [id]);

  const handleSave = async () => {
    const token = localStorage.getItem("token");
    const data = {
      name,
      cnpj,
      contas: [
        { codigoConta: codigoConta1, saldoDia: saldoDia1 },
        { codigoConta: codigoConta2, saldoDia: saldoDia2 },
      ],
      tipoRemessa,
    };

    try {
      await axios.put(`http://localhost:8000/files.php?id=${id}`, data, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Arquivo atualizado com sucesso!");
      window.location.href = "/files";
    } catch (error) {
      console.error("Erro ao atualizar arquivo:", error);
      alert("Não foi possível atualizar o arquivo.");
    }
  };

  if (loading) {
    return <div>Carregando...</div>;
  }

  return (
    <div style={styles.container}>
      <Header />
      <h1 style={styles.title}>Editar Arquivo</h1>
      <form onSubmit={(e) => e.preventDefault()}>
        <input
          type="text"
          placeholder="Nome do Arquivo"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={styles.input}
          required
        />
        <input
          type="text"
          placeholder="CNPJ"
          value={cnpj}
          onChange={(e) => setCnpj(e.target.value)}
          style={styles.input}
          required
        />
        <h3>Conta 1</h3>
        <input
          type="text"
          placeholder="Código Conta 1"
          value={codigoConta1}
          onChange={(e) => setCodigoConta1(e.target.value)}
          style={styles.input}
          required
        />
        <input
          type="number"
          placeholder="Saldo do Dia"
          value={saldoDia1}
          onChange={(e) => setSaldoDia1(e.target.value)}
          style={styles.input}
          required
        />
        <h3>Conta 2</h3>
        <input
          type="text"
          placeholder="Código Conta 2"
          value={codigoConta2}
          onChange={(e) => setCodigoConta2(e.target.value)}
          style={styles.input}
          required
        />
        <input
          type="number"
          placeholder="Saldo do Dia"
          value={saldoDia2}
          onChange={(e) => setSaldoDia2(e.target.value)}
          style={styles.input}
          required
        />
        <label style={styles.label}>
          Tipo de Remessa:
          <input
            type="checkbox"
            checked={tipoRemessa === "I"}
            onChange={() => setTipoRemessa(tipoRemessa === "I" ? "O" : "I")}
            style={styles.checkbox}
          />
          I
        </label>
        <button type="button" style={styles.saveButton} onClick={handleSave}>
          Salvar
        </button>
      </form>
    </div>
  );
}

const styles = {
  container: {
    padding: "20px",
    backgroundColor: "#32373C",
    color: "#FFFFFF",
    minHeight: "100vh",
  },
  title: {
    textAlign: "center",
    color: "#FFFFFF",
    marginBottom: "20px",
  },
  input: {
    display: "block",
    width: "100%",
    padding: "10px",
    marginBottom: "20px",
    borderRadius: "5px",
    border: "1px solid #ddd",
  },
  label: {
    display: "block",
    marginBottom: "20px",
    color: "#FFFFFF",
  },
  checkbox: {
    marginLeft: "10px",
  },
  saveButton: {
    backgroundColor: "#4CAF50",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default EditFile;
