import React, { useState } from "react";
import axios from "axios";
import Header from "./Header";

function CreateFile() {
  const [name, setName] = useState("");
  const [cnpj, setCnpj] = useState("");
  const [codigoConta1, setCodigoConta1] = useState("");
  const [saldoDia1, setSaldoDia1] = useState("");
  const [codigoConta2, setCodigoConta2] = useState("");
  const [saldoDia2, setSaldoDia2] = useState("");
  const [tipoRemessa, setTipoRemessa] = useState("I"); // Valor padrão como "I"

  const handleCreateFile = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const data = {
      name,
      cnpj,
      contas: [
        { codigoConta: codigoConta1, saldoDia: saldoDia1 },
        { codigoConta: codigoConta2, saldoDia: saldoDia2 },
      ],
      tipoRemessa,
    };

    try {
      await axios.post(
        "http://localhost:8000/files.php",
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      alert("Arquivo criado com sucesso!");
      window.location.href = "/files"; // Redireciona para a lista de arquivos
    } catch (error) {
      console.error("Erro ao criar arquivo:", error);
      alert("Erro ao criar arquivo. Tente novamente.");
    }
  };

  return (
    <div>
      <Header />
      <h1>Lançamento 4111</h1>
      <form onSubmit={handleCreateFile}>
        <input
          type="text"
          placeholder="Nome do Arquivo"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="CNPJ"
          value={cnpj}
          onChange={(e) => setCnpj(e.target.value)}
          required
        />
        <h3>Conta 1</h3>
        <input
          type="text"
          placeholder="Código Conta 1"
          value={codigoConta1}
          onChange={(e) => setCodigoConta1(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Saldo do Dia"
          value={saldoDia1}
          onChange={(e) => setSaldoDia1(e.target.value)}
          required
        />
        <h3>Conta 2</h3>
        <input
          type="text"
          placeholder="Código Conta 2"
          value={codigoConta2}
          onChange={(e) => setCodigoConta2(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Saldo do Dia"
          value={saldoDia2}
          onChange={(e) => setSaldoDia2(e.target.value)}
          required
        />
        <label>
          Tipo de Remessa:
          <input
            type="checkbox"
            checked={tipoRemessa === "I"}
            onChange={() => setTipoRemessa(tipoRemessa === "I" ? "O" : "I")} // Alterna entre "I" e "O"
          />
          I
        </label>
        <button type="submit">Criar</button>
      </form>
    </div>
  );
}

export default CreateFile;
