import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./Header";
import { useParams } from "react-router-dom";

function EditFile() {
  const { id } = useParams(); // Obtém o ID da URL
  const [name, setName] = useState("");
  const [fileData, setFileData] = useState(null);

  useEffect(() => {
    const fetchFile = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get(`http://localhost:8000/files.php?id=${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFileData(response.data); // Armazena os dados do arquivo
        setName(response.data.name); // Define o nome do arquivo no formulário
      } catch (error) {
        console.error("Erro ao buscar arquivo:", error);
        alert("Erro ao carregar arquivo.");
      }
    };

    fetchFile();
  }, [id]);

  const handleSave = async () => {
    const token = localStorage.getItem("token");
    try {
      await axios.put(`http://localhost:8000/files.php?id=${id}`, { name }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Arquivo atualizado com sucesso!");
      window.location.href = "/files";
    } catch (error) {
      console.error("Erro ao atualizar arquivo:", error);
      alert("Não foi possível atualizar o arquivo.");
    }
  };

  if (!fileData) {
    return <div>Carregando...</div>;
  }

  return (
    <div style={styles.container}>
      <Header />
      <h1 style={styles.title}>Editar</h1>
      <form onSubmit={handleCreateFile}>
        <input
          type="text"
          placeholder="Nome do Arquivo"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="CNPJ"
          value={cnpj}
          onChange={(e) => setCnpj(e.target.value)}
          required
        />
        <h3>Conta 1</h3>
        <input
          type="text"
          placeholder="Código Conta 1"
          value={codigoConta1}
          onChange={(e) => setCodigoConta1(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Saldo do Dia"
          value={saldoDia1}
          onChange={(e) => setSaldoDia1(e.target.value)}
          required
        />
        <h3>Conta 2</h3>
        <input
          type="text"
          placeholder="Código Conta 2"
          value={codigoConta2}
          onChange={(e) => setCodigoConta2(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Saldo do Dia"
          value={saldoDia2}
          onChange={(e) => setSaldoDia2(e.target.value)}
          required
        />
        <label>
          Tipo de Remessa:
          <input
            type="checkbox"
            checked={tipoRemessa === "I"}
            onChange={() => setTipoRemessa(tipoRemessa === "I" ? "O" : "I")} // Alterna entre "I" e "O"
          />
          I
        </label>
        <button type="submit">Criar</button>
      </form>
      <button style={styles.saveButton} onClick={handleSave}>
        Salvar
      </button>
    </div>
  );
}

const styles = {
  container: {
    padding: "20px",
    backgroundColor: "#32373C",
    color: "#FFFFFF",
    minHeight: "100vh",
  },
  title: {
    textAlign: "center",
    color: "#FFFFFF",
    marginBottom: "20px",
  },
  input: {
    display: "block",
    width: "100%",
    padding: "10px",
    marginBottom: "20px",
    borderRadius: "5px",
    border: "1px solid #ddd",
  },
  saveButton: {
    backgroundColor: "#4CAF50",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default EditFile;
