import React, { useState } from "react";
import axios from "axios";
import Header from "./Header";

function CreateFile() {
  const [name, setName] = useState("");
  const [cnpj, setCnpj] = useState("");
  const [codigoConta1, setCodigoConta1] = useState("");
  const [saldoDia1, setSaldoDia1] = useState("");
  const [codigoConta2, setCodigoConta2] = useState("");
  const [saldoDia2, setSaldoDia2] = useState("");
  const [tipoRemessa, setTipoRemessa] = useState("I"); // Valor padrão como "I"

  const handleCreateFile = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    const data = {
      name,
      cnpj,
      contas: [
        { codigoConta: codigoConta1, saldoDia: saldoDia1 },
        { codigoConta: codigoConta2, saldoDia: saldoDia2 },
      ],
      tipoRemessa,
    };

    try {
      await axios.post("http://localhost:8000/files.php", data, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      alert("Arquivo criado com sucesso!");
      window.location.href = "/files"; // Redireciona para a lista de arquivos
    } catch (error) {
      console.error("Erro ao criar arquivo:", error);
      alert("Erro ao criar arquivo. Tente novamente.");
    }
  };

  return (
    <div style={styles.container}>
      <Header />
      <h1 style={styles.title}>Lançamento 4111</h1>
      <form onSubmit={handleCreateFile} style={styles.form}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Tipo de Remessa</label>
          <select
            value={tipoRemessa}
            onChange={(e) => setTipoRemessa(e.target.value)}
            style={styles.select}
            required
          >
            <option value="I">I (Primeira remessa do documento)</option>
            <option value="S">S (Substituir documento enviado e aceito)</option>
          </select>
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>Nome do Arquivo</label>
          <input
            type="text"
            placeholder="Nome do Arquivo"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={styles.input}
            required
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>CNPJ</label>
          <input
            type="text"
            placeholder="CNPJ"
            value={cnpj}
            onChange={(e) => setCnpj(e.target.value)}
            style={styles.input}
            required
          />
        </div>
        <h3 style={styles.subtitle}>Conta 1</h3>
        <div style={styles.row}>
          <input
            type="text"
            placeholder="Código Conta 1"
            value={codigoConta1}
            onChange={(e) => setCodigoConta1(e.target.value)}
            style={styles.inputInline}
            required
          />
          <input
            type="number"
            placeholder="Saldo do Dia"
            value={saldoDia1}
            onChange={(e) => setSaldoDia1(e.target.value)}
            style={styles.inputInline}
            required
          />
        </div>
        <h3 style={styles.subtitle}>Conta 2</h3>
        <div style={styles.row}>
          <input
            type="text"
            placeholder="Código Conta 2"
            value={codigoConta2}
            onChange={(e) => setCodigoConta2(e.target.value)}
            style={styles.inputInline}
            required
          />
          <input
            type="number"
            placeholder="Saldo do Dia"
            value={saldoDia2}
            onChange={(e) => setSaldoDia2(e.target.value)}
            style={styles.inputInline}
            required
          />
        </div>
        <button type="submit" style={styles.submitButton}>
          Criar
        </button>
      </form>
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: "#32373C",
    color: "#FFFFFF",
    minHeight: "100vh",
    padding: "20px",
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
    fontSize: "2rem",
    color: "#FFFFFF",
  },
  form: {
    maxWidth: "600px",
    margin: "0 auto",
    backgroundColor: "#444B52",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
  },
  formGroup: {
    marginBottom: "15px",
  },
  label: {
    display: "block",
    marginBottom: "5px",
    color: "#FFFFFF",
  },
  input: {
    width: "96.5%",
    padding: "10px",
    borderRadius: "5px",
    border: "1px solid #ddd",
    fontSize: "1rem",
  },
  inputInline: {
    width: "50%", // Divide os inputs igualmente na linha
    padding: "10px",
    borderRadius: "5px",
    border: "1px solid #ddd",
    fontSize: "1rem",
    marginRight: "1%",
  },
  row: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: "15px",
  },
  select: {
    width: "100%",
    padding: "10px",
    borderRadius: "5px",
    border: "1px solid #ddd",
    fontSize: "1rem",
    backgroundColor: "#FFFFFF",
    color: "#000000",
  },
  subtitle: {
    marginTop: "20px",
    color: "#FFFFFF",
  },
  submitButton: {
    width: "100%",
    padding: "10px",
    backgroundColor: "#4CAF50",
    color: "#FFFFFF",
    border: "none",
    borderRadius: "5px",
    fontSize: "1rem",
    cursor: "pointer",
  },
};

export default CreateFile;
