import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "./Header";

function FileList() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      alert("Você precisa estar logado para acessar esta página.");
      window.location.href = "/";
      return;
    }

    const fetchFiles = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/files.php", {
          headers: {
            Authorization: `Bearer ${token}`, // Envia o token no cabeçalho
          },
        });
        setFiles(response.data);
      } catch (error) {
        console.error("Erro ao buscar arquivos:", error);
        alert("Erro ao carregar arquivos. Faça login novamente.");
        window.location.href = "/";
      }
    };

    fetchFiles();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove o token do localStorage
    alert("Sessão encerrada com sucesso!");
    window.location.href = "/"; // Redireciona para a tela de login
  };

  const handleDownload = (id) => {
    window.open(`http://127.0.0.1:8000/files.php?download=1&id=${id}`);
  };

  const handleCreateNewFile = () => {
    window.location.href = "/create";
  };

  const handleDelete = async (id) => {
    const confirm = window.confirm("Tem certeza que deseja excluir este arquivo?");
    if (confirm) {
      try {
        const token = localStorage.getItem("token");

        // Faz a requisição DELETE para o backend
        await axios.delete("http://127.0.0.1:8000/files.php", {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
          data: { id }, // Envia o ID no corpo da requisição
        });

        // Remove o arquivo da lista no front-end
        setFiles(files.filter((file) => file.id !== id));

        alert("Arquivo deletado com sucesso!");
      } catch (error) {
        console.error("Erro ao deletar arquivo:", error);
        alert("Não foi possível deletar o arquivo.");
      }
    }
  };


  const handleEdit = (id) => {
    window.location.href = `/edit/${id}`; // Redireciona para a página de edição
  };

  return (
    <div style={styles.container}>
      <Header />
      <div style={styles.headerContainer}>
        <h1 style={styles.title}>Listagem 4111</h1>
      </div>
      <div style={styles.buttonRow}>
        <button style={styles.createButton} onClick={handleCreateNewFile}>
          Lançar Novo Arquivo
        </button>
        <button style={styles.logoutButton} onClick={handleLogout}>
          Logout
        </button>
      </div>
      <ul style={styles.list}>
        {files.map((file) => (
          <li key={file.id} style={styles.listItem}>
            <span style={styles.fileName}>{file.name}</span>
            <div>
              <button style={styles.downloadButton} onClick={() => handleDownload(file.id)}>
                Gerar XML
              </button>
              <button style={styles.editButton} onClick={() => handleEdit(file.id)}>
                Editar
              </button>
              <button style={styles.deleteButton} onClick={() => handleDelete(file.id)}>
                Deletar
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: "#32373C",
    color: "#FFFFFF",
    minHeight: "100vh",
    padding: "20px",
  },
  headerContainer: {
    marginBottom: "20px",
  },
  title: {
    margin: 0,
    color: "#FFFFFF",
    fontSize: "2rem",
    textAlign: "center",
  },
  buttonRow: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "20px",
  },
  createButton: {
    backgroundColor: "#4CAF50",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 15px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "1rem",
  },
  logoutButton: {
    backgroundColor: "#d9534f",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 15px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "1rem",
  },
  list: {
    listStyleType: "none",
    padding: 0,
    margin: 0,
  },
  listItem: {
    backgroundColor: "#444B52",
    padding: "15px",
    margin: "10px 0",
    borderRadius: "5px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    color: "#FFFFFF",
  },
  fileName: {
    fontSize: "1rem",
    color: "#FFFFFF",
  },
  downloadButton: {
    backgroundColor: "#2196F3",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 15px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "0.9rem",
    marginRight: "10px",
  },
  editButton: {
    backgroundColor: "#FFC107",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 15px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "0.9rem",
    marginRight: "10px",
  },
  deleteButton: {
    backgroundColor: "#F44336",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 15px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "0.9rem",
  },
};

export default FileList;
