import React, { useEffect, useState } from "react";
import axios from "axios";

function FileList() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      alert("Você precisa estar logado para acessar esta página.");
      window.location.href = "/";
      return;
    }

    const fetchFiles = async () => {
      try {
        const response = await axios.get("http://localhost:8000/files.php", {
          headers: {
            Authorization: `Bearer ${token}`, // Envia o token no cabeçalho
          },
        });
        setFiles(response.data);
      } catch (error) {
        console.error("Erro ao buscar arquivos:", error);
        alert("Erro ao carregar arquivos. Faça login novamente.");
        window.location.href = "/";
      }
    };

    fetchFiles();
  }, []);

  const handleDownload = (id) => {
    // Redireciona para o endpoint de download do backend
    window.open(`http://localhost:8000/files.php?download=1&id=${id}`);
  };

  const handleCreateNewFile = () => {
    // Redireciona para a página de criação de novos arquivos
    window.location.href = "/create";
  };

  return (
    <div>
      <h1>Listagem 4111</h1>
      <button onClick={handleCreateNewFile}>Lançar Novo Arquivo</button>
      <ul>
        {files.map((file) => (
          <li key={file.id}>
            <span>{file.name}</span>
            <button onClick={() => handleDownload(file.id)}>Gerar XML</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FileList;
