import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./Header";
import { useParams } from "react-router-dom";

function EditFile() {
  const { id } = useParams(); // Pega o parâmetro :id da URL
  const [name, setName] = useState("");
  const [fileData, setFileData] = useState(null);

  useEffect(() => {
    const fetchFile = async () => {
      const token = localStorage.getItem("token");
      try {
        const response = await axios.get(`http://localhost:8000/files.php?id=${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFileData(response.data);
        setName(response.data.name);
      } catch (error) {
        console.error("Erro ao buscar arquivo:", error);
        alert("Erro ao carregar arquivo.");
      }
    };

    fetchFile();
  }, [id]);

  const handleSave = async () => {
    const token = localStorage.getItem("token");
    try {
      await axios.put(`http://localhost:8000/files.php?id=${id}`, { name }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Arquivo atualizado com sucesso!");
      window.location.href = "/files";
    } catch (error) {
      console.error("Erro ao atualizar arquivo:", error);
      alert("Não foi possível atualizar o arquivo.");
    }
  };

  if (!fileData) {
    return <div>Carregando...</div>;
  }

  return (
    <div style={styles.container}>
      <Header />
      <h1 style={styles.title}>Editar Arquivo</h1>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={styles.input}
      />
      <button style={styles.saveButton} onClick={handleSave}>
        Salvar
      </button>
    </div>
  );
}

const styles = {
  container: {
    padding: "20px",
    backgroundColor: "#32373C",
    color: "#FFFFFF",
    minHeight: "100vh",
  },
  title: {
    textAlign: "center",
    color: "#FFFFFF",
    marginBottom: "20px",
  },
  input: {
    display: "block",
    width: "100%",
    padding: "10px",
    marginBottom: "20px",
    borderRadius: "5px",
    border: "1px solid #ddd",
  },
  saveButton: {
    backgroundColor: "#4CAF50",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default EditFile;
