import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "./Header";

function FileList() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      alert("Você precisa estar logado para acessar esta página.");
      window.location.href = "/";
      return;
    }

    const fetchFiles = async () => {
      try {
        const response = await axios.get("http://localhost:8000/files.php", {
          headers: {
            Authorization: `Bearer ${token}`, // Envia o token no cabeçalho
          },
        });
        setFiles(response.data);
      } catch (error) {
        console.error("Erro ao buscar arquivos:", error);
        alert("Erro ao carregar arquivos. Faça login novamente.");
        window.location.href = "/";
      }
    };

    fetchFiles();
  }, []);

  const handleDownload = (id) => {
    // Redireciona para o endpoint de download do backend
    window.open(`http://localhost:8000/files.php?download=1&id=${id}`);
  };

  const handleCreateNewFile = () => {
    // Redireciona para a página de criação de novos arquivos
    window.location.href = "/create";
  };

  return (
    <div style={styles.container}>
      <Header />
      <h1 style={styles.title}>Listagem 4111</h1>
      <button style={styles.createButton} onClick={handleCreateNewFile}>
        Lançar Novo Arquivo
      </button>
      <ul style={styles.list}>
        {files.map((file) => (
          <li key={file.id} style={styles.listItem}>
            <span style={styles.fileName}>{file.name}</span>
            <button style={styles.downloadButton} onClick={() => handleDownload(file.id)}>
              Gerar XML
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: "#32373C",
    color: "#FFFFFF",
    minHeight: "100vh",
    padding: "20px",
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
    color: "#FFFFFF",
    fontSize: "2rem",
  },
  createButton: {
    backgroundColor: "#4CAF50",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "1rem",
    marginBottom: "20px",
    display: "block",
    margin: "0 auto",
  },
  list: {
    listStyleType: "none",
    padding: 0,
    margin: 0,
  },
  listItem: {
    backgroundColor: "#444B52",
    padding: "15px",
    margin: "10px 0",
    borderRadius: "5px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    color: "#FFFFFF",
  },
  fileName: {
    fontSize: "1rem",
    color: "#FFFFFF",
  },
  downloadButton: {
    backgroundColor: "#2196F3",
    color: "#FFFFFF",
    border: "none",
    padding: "10px 15px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "0.9rem",
  },
};

export default FileList;
