import React, { useEffect, useState } from "react";
import api from "../api/api";

function FileList({ token }) {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await api.get("/files.php", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFiles(response.data);
      } catch (error) {
        console.error("Erro ao buscar arquivos:", error);
      }
    };

    fetchFiles();
  }, [token]);

  const handleDelete = async (id) => {
    try {
      await api.delete(`/files.php?id=${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFiles(files.filter((file) => file.id !== id));
    } catch (error) {
      console.error("Erro ao excluir arquivo:", error);
    }
  };

  return (
    <div>
      <h1>Lista de Arquivos</h1>
      <button onClick={() => (window.location.href = "/create")}>
        Criar Novo Arquivo
      </button>
      <ul>
        {files.map((file) => (
          <li key={file.id}>
            <strong>{file.name}</strong>
            <button onClick={() => (window.location.href = `/edit/${file.id}`)}>
              Editar
            </button>
            <button
              onClick={() =>
                window.open(`http://localhost:8000/files.php?id=${file.id}/download`)
              }
            >
              Baixar
            </button>
            <button onClick={() => handleDelete(file.id)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FileList;
